// src/Register.js
import { TextInput, Button, Group, Box } from '@mantine/core';
import { useForm } from '@mantine/form';
import axios from 'axios';

export default function Register() {
  const form = useForm({
    initialValues: {
      username: '',
      password: ''
    },

    // Validate input fields using regex
    validate: {
      username: (value) => (/^\S+$/.test(value) ? null : 'Invalid username (has whitespace or is empty)'),
      password: (value) => (/^\S{6}\S*$/.test(value) ? null : 'Invalid password (has whitespace or less than 6 characters)')
    },
  });

  const handleRegister = async () => {
    try {
      // Make a request to the server to handle registration
      await axios.post('http://localhost:5000/api/register', {
        username: form.values.username,
        password: form.values.password
      });
      console.log('Registration successful');
    } catch (error) {
      console.error('Registration failed', error.response.data.message);
    }
  };

  return (
    <Box maw={340} mx="auto">
      <form onSubmit={form.onSubmit(handleRegister)}>
        <TextInput
          label="Username"
          placeholder="Your username"
          {...form.getInputProps('username')}
        />

        <TextInput
          label="Password"
          placeholder="Your password"
          type="password"
          {...form.getInputProps('password')}
        />

        <Group justify="flex-end" mt="md">
          <Button type="submit">Register</Button>
        </Group>
      </form>
    </Box>
  );
}
