// src/InteractiveClock.js
import React, { useState, useEffect } from 'react';
import './InteractiveClock.css';

const InteractiveClock = () => {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTime(new Date());
    }, 1000);

    // Clean up the interval when the component unmounts
    return () => clearInterval(intervalId);
  }, []);

  const getHandRotation = (handType) => {
    const rotationUnit = handType === 'hour' ? 360 / 12 : 360 / 60;
    const currentValue = handType === 'hour' ? time.getHours() : time.getMinutes();
    return `rotate(${90 + currentValue * rotationUnit}deg)`;
  };

  return (
    <div className="interactive-clock">
      <div className="clock-face">
        <div className="hour-hand" style={{ transform: getHandRotation('hour') }}></div>
        <div className="minute-hand" style={{ transform: getHandRotation('minute') }}></div>
      </div>
    </div>
  );
};

export default InteractiveClock;
